require("Script/handleMessage")function sendExchangeParameterReq(myid, chapterid, instancetype) --构造消息，repeat字段需要用一个表来表示，即使只有一项，如下面的param字段--更多如何构造消息的内容，请参考例子Script/Pbc/test.lua--// 钻石系统协议--message CSExchangeParameterReq {--	required uint32 id					= 1;				// 钻石相关id--	repeated uint32 param				= 2;				// 相关参数(如: 星座开槽[1-12]、装备开孔[传装备的pos]、精英副本章节重置等)--}	local msgname="CSExchangeParameterReq"	ExchangeParameterReq = {		id = myid,		param = {			chapterid,			instancetype		}	}	local ret = send_message(msgname, ExchangeParameterReq, true)		return ret;end--处理ExchangeParameterRspfunction CSExchangeParameterRspCallBack(msg) 	print("Rcv CSExchangeParameterRsp msg")	print(msg.succ, msg.id)	for _,v in ipairs(msg.param) do		print("\tparam:"..v)			end	return falseendfunction addcallbackExchangeParameterRsp()	addMsgCallBack("CSExchangeParameterRsp", CSExchangeParameterRspCallBack)	return 1endfunction showJingYingPlayTimes(playtimes)	return "精英副本剩余挑战次数："..(3-playtimes).."/3"end